$(function(){
	$(this).keyup(function(e) {
			e.preventDefault();
			e.stopPropagation();
			if(e.ctrlKey && e.shiftKey && e.keyCode==65){
			$(location).attr('href',$('#new_controller_path').val())}
});
	$('input:checkbox').keyup(function(e) {
		e.preventDefault();
		e.stopPropagation();
		if(e.keyCode==32){
			if ($(this).prop('checked') == true){
				$(this).prop('checked', false);}
			else{
				$(this).prop('checked', true);
			}
		}
	});
	$('select').chosen({width: "100%"});
	$('#establecimientos').change(function(){	
		cambioEst();
	});
$('#empresas').change(function(){	
	cambioEmp();
});


});

function cambioEst(){
$.ajax({
	global: false,
	type: "POST",
	url: "/sessions/cambioEst",
	data: {establecimiento_id: $('#establecimientos').val()},
 	dataType: "script"
	});
};

function cambioEmp(){
$.ajax({
	global: false,
	type: "POST",
	url: "/sessions/cambioEmp",
	data: {empresa_id: $('#empresas').val()},
	dataType: "script",
});
};

